-- ----------------------------
--  Table structure for `city_water`
-- ----------------------------
DROP TABLE IF EXISTS `city_water`;
CREATE TABLE `city_water` (
  `province` varchar(255) DEFAULT NULL COMMENT '省份',
  `city` varchar(255) DEFAULT NULL,
  `longitude` varchar(255) DEFAULT NULL,
  `latitude` varchar(255) DEFAULT NULL,
  `water_point` varchar(255) NOT NULL COMMENT '监测点',
  `pinyin` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`water_point`),
  KEY `water_point` (`water_point`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `economy`
-- ----------------------------
DROP TABLE IF EXISTS `economy`;
CREATE TABLE `economy` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `city` varchar(255) DEFAULT NULL,
  `time` timestamp NULL DEFAULT NULL,
  `gdp` varchar(255) DEFAULT NULL,
  `NationalIn` varchar(255) DEFAULT NULL,
  `IndustryPr` varchar(255) DEFAULT NULL,
  `TertiaryIn` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id_UNIQUE` (`id`),
  KEY `city` (`city`),
  CONSTRAINT `FKpvk4dri410d6vbprytkqen8ff` FOREIGN KEY (`city`) REFERENCES `city` (`city`),
  CONSTRAINT `eco_fk` FOREIGN KEY (`city`) REFERENCES `city` (`city`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `water`
-- ----------------------------
DROP TABLE IF EXISTS `water`;
CREATE TABLE `water` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `water_point` varchar(255) DEFAULT NULL COMMENT '监测点',
  `ph` float(18,0) DEFAULT NULL,
  `d_o` float DEFAULT NULL,
  `cod` float(18,0) DEFAULT NULL,
  `nh3_n` float(18,0) DEFAULT NULL,
  `to_week` char(4) DEFAULT NULL COMMENT '本周水质',
  `pre_week` char(4) DEFAULT NULL COMMENT '上周水质',
  PRIMARY KEY (`id`),
  UNIQUE KEY `id_UNIQUE` (`id`),
  KEY `waterp_fk_idx` (`water_point`),
  CONSTRAINT `FKmfqkf1hb9q6shj9v1805i361o` FOREIGN KEY (`water_point`) REFERENCES `city_water` (`water_point`),
  CONSTRAINT `water_fk` FOREIGN KEY (`water_point`) REFERENCES `city_water` (`water_point`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `weather`
-- ----------------------------
DROP TABLE IF EXISTS `weather`;
CREATE TABLE `weather` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `city` varchar(255) DEFAULT NULL,
  `time` timestamp NULL DEFAULT NULL,
  `weather` varchar(255) DEFAULT NULL COMMENT '天气概况',
  `temperature` float(18,0) DEFAULT NULL COMMENT '温度',
  `humidity` varchar(255) DEFAULT NULL COMMENT '湿度',
  `visibility` varchar(255) DEFAULT NULL COMMENT '能见度',
  `wind_scale` float(18,0) DEFAULT NULL COMMENT '风力（等级）',
  `wind_dir_degree` float(18,0) DEFAULT NULL COMMENT '风向角度',
  `cloud_rate` varchar(255) DEFAULT NULL COMMENT '云量',
  `wind_speed` float(18,0) DEFAULT NULL COMMENT '风速',
  `wind_dir` float(18,0) DEFAULT NULL COMMENT '风向。单位是度。正北方向为0度，顺时针增加到360度。',
  PRIMARY KEY (`id`),
  UNIQUE KEY `id_UNIQUE` (`id`),
  KEY `city` (`city`),
  CONSTRAINT `FK60rsvxw9rqv6afskx66p8xqxm` FOREIGN KEY (`city`) REFERENCES `city` (`city`),
  CONSTRAINT `wea_fk` FOREIGN KEY (`city`) REFERENCES `city` (`city`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

SET FOREIGN_KEY_CHECKS = 1;
